import React, { useState } from 'react';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import LoginPage from './components/LoginPage';
import RegisterPage from './components/RegisterPage';
import Dashboard from './components/Dashboard';
import Chatbot from './components/Chatbot';
import Monitoring from './components/Monitoring';
import Alerts from './components/Alerts';
import Education from './components/Education';
import Diet from './components/Diet';
import Medication from './components/Medication';
import Profile from './components/Profile';

const AppContent = () => {
  const { isAuthenticated, loading, user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(true);
  const [showRegister, setShowRegister] = useState(false);

  // Show loading state while checking authentication
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-primary-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto mb-4"></div>
          <p className="text-primary-600">Loading...</p>
        </div>
      </div>
    );
  }

  // Show login or register page if not authenticated
  if (!isAuthenticated) {
    if (showRegister) {
      return <RegisterPage onSwitchToLogin={() => setShowRegister(false)} />;
    }
    return <LoginPage onLoginSuccess={() => console.log('Login successful')} onSwitchToRegister={() => setShowRegister(true)} />;
  }

  const navigation = [
    { id: 'dashboard', name: 'Dashboard', icon: 'üìä', component: Dashboard },
    { id: 'chatbot', name: 'Health Assistant', icon: 'üí¨', component: Chatbot },
    { id: 'monitoring', name: 'Health Tracking', icon: 'üìà', component: Monitoring },
    { id: 'alerts', name: 'Alerts & Notifications', icon: 'üîî', component: Alerts },
    { id: 'education', name: 'Educational Resources', icon: 'üìö', component: Education },
    { id: 'diet', name: 'Diet Chart', icon: 'üçΩÔ∏è', component: Diet },
    { id: 'medication', name: 'Medication Support', icon: 'üíä', component: Medication },
    { id: 'profile', name: 'Profile', icon: 'üë§', component: Profile }
  ];

  const renderActiveComponent = () => {
    const navItem = navigation.find(nav => nav.id === activeTab);
    if (navItem && navItem.component) {
      const Component = navItem.component;
      return <Component />;
    }
    return <Dashboard />;
  };

  return (
    <div className="min-h-screen bg-primary-50 flex flex-col">
      {/* Mobile menu overlay */}
      {sidebarOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden" onClick={() => setSidebarOpen(false)} />
      )}

      <div className="flex flex-1">
              {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 glass shadow-elegant transform transition-all duration-500 ease-out
        ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'}
        ${sidebarCollapsed ? 'w-20' : 'w-72'}
        lg:translate-x-0 lg:static lg:inset-0`}
      >
        <div className="flex items-center justify-center h-20 px-6 border-b border-white/20">
          <button
            onClick={() => setSidebarCollapsed((prev) => !prev)}
            className="flex items-center space-x-3 select-none hover:bg-white/10 rounded-2xl p-3 transition-all duration-300 hover-lift"
            aria-label="Toggle sidebar"
          >
            <div className="w-10 h-10 bg-gradient-to-br from-secondary-500 to-accent-500 rounded-xl flex items-center justify-center shadow-soft">
              <span className="text-white text-lg font-bold">H</span>
            </div>
            {!sidebarCollapsed && (
              <span className="text-xl font-bold text-neutral-800 tracking-wide transition-all duration-500">HealthHub</span>
            )}
          </button>
        </div>
        <nav className="mt-8 px-4 flex flex-col space-y-2">
          {navigation.map((item, index) => (
            <button
              key={item.id}
              onClick={() => {
                setActiveTab(item.id);
                setSidebarOpen(false);
              }}
              className={`group flex items-center space-x-4 px-4 py-4 rounded-2xl transition-all duration-300 w-full hover-lift
                ${activeTab === item.id 
                  ? 'bg-gradient-to-r from-secondary-500 to-accent-500 text-white shadow-elegant' 
                  : 'text-neutral-600 hover:bg-white/50 hover:text-neutral-800'
                }
                ${sidebarCollapsed ? 'justify-center px-2' : 'justify-start'}
                fade-in
              `}
              style={{ animationDelay: `${index * 0.1}s` }}
            >
              <span className={`text-xl transition-transform duration-300 ${activeTab === item.id ? 'scale-110' : 'group-hover:scale-105'}`}>
                {item.icon}
              </span>
              {!sidebarCollapsed && (
                <span className="font-medium transition-all duration-300">{item.name}</span>
              )}
            </button>
          ))}
        </nav>
      </div>

        {/* Main content */}
        <div className={`flex-1 min-h-screen flex flex-col transition-all duration-500 ease-out ${sidebarCollapsed ? 'lg:ml-20' : 'lg:ml-72'}`}>
          {/* Header */}
          <header className="glass-header border-b border-white/20 shadow-elegant header-glow relative overflow-hidden">
            {/* Background gradient overlay */}
            <div className="absolute inset-0 bg-gradient-to-r from-secondary-500/15 via-accent-500/15 to-secondary-500/15 animate-pulse"></div>
            
            <div className="flex items-center justify-between h-20 px-4 sm:px-6 lg:px-8 relative z-10">
              {/* Mobile Menu Button */}
              <button
                onClick={() => setSidebarOpen(true)}
                className="lg:hidden text-neutral-600 hover:text-neutral-800 p-3 rounded-2xl hover:bg-white/20 transition-all duration-300 hover-lift ripple-effect"
              >
                <span className="text-2xl">‚ò∞</span>
              </button>

              {/* Main Header Content */}
              <div className="flex-1 flex items-center justify-center">
                <div className="glass-header rounded-2xl shadow-elegant px-6 py-4 max-w-4xl w-full hover-lift border border-white/20 status-pulse">
                  <div className="flex items-center justify-between">
                    {/* Left Section - User Info */}
                    <div className="flex items-center space-x-4">
                      {/* User Avatar */}
                      <div className="relative group">
                        <div className="w-14 h-14 bg-gradient-to-br from-secondary-500 via-accent-500 to-secondary-600 rounded-2xl flex items-center justify-center shadow-elegant transition-all duration-300 avatar-hover">
                          <span className="text-white text-xl font-bold">
                            {user?.profile?.firstName?.[0] || user?.email?.[0] || 'üë§'}
                          </span>
                        </div>
                        <div className="absolute -bottom-1 -right-1 w-5 h-5 bg-gradient-to-br from-accent-400 to-accent-600 rounded-full border-2 border-white flex items-center justify-center shadow-soft float-animation">
                          <span className="text-white text-xs">‚úì</span>
                        </div>
                        {/* Online indicator */}
                        <div className="absolute top-0 right-0 w-4 h-4 bg-green-400 rounded-full border-2 border-white animate-pulse"></div>
                      </div>

                      {/* User Info */}
                      <div>
                        <h1 className="text-xl sm:text-2xl font-bold gradient-text leading-tight">
                          Welcome back, <span className="text-white">{user?.profile?.firstName || user?.profile?.username || 'User'}</span>!
                        </h1>
                        <div className="flex items-center space-x-3 mt-1">
                          <span className="text-sm text-neutral-600 font-medium">
                            @{user?.profile?.username || user?.email}
                          </span>
                          {user?.profile?.age && (
                            <>
                              <span className="text-xs text-neutral-400">‚Ä¢</span>
                              <span className="text-sm text-neutral-500">
                                {user.profile.age} years old
                              </span>
                            </>
                          )}
                          {user?.profile?.gender && (
                            <>
                              <span className="text-xs text-neutral-400">‚Ä¢</span>
                              <span className="text-sm text-neutral-500">
                                {user.profile.gender}
                              </span>
                            </>
                          )}
                        </div>
                      </div>
                    </div>

                    {/* Right Section - Status and Actions */}
                    <div className="flex items-center space-x-3">
                      {/* Status Badge */}
                      <div className="flex flex-col items-end space-y-1">
                        <div className="bg-gradient-to-r from-accent-500 to-accent-600 text-white px-4 py-2 rounded-xl shadow-soft flex items-center space-x-2 float-animation">
                          <div className="w-2 h-2 bg-white rounded-full animate-pulse"></div>
                          <span className="text-sm font-semibold">Active</span>
                        </div>
                        <div className="text-xs text-neutral-500 text-center font-medium">
                          {user?.createdAt ? `Member since ${new Date(user.createdAt).getFullYear()}` : 'New Member'}
                        </div>
                      </div>

                      {/* Divider */}
                      <div className="hidden sm:block w-px h-8 bg-gradient-to-b from-transparent via-neutral-300 to-transparent"></div>

                      {/* Action Buttons */}
                      <div className="flex items-center space-x-2">
                        {/* Notification Bell */}
                        <button className="relative p-3 text-neutral-600 hover:text-neutral-800 hover:bg-white/20 rounded-xl transition-all duration-300 hover-lift group ripple-effect">
                          <span className="text-xl">üîî</span>
                          {/* Notification badge */}
                          <div className="absolute -top-1 -right-1 w-5 h-5 bg-red-500 rounded-full flex items-center justify-center shadow-soft animate-bounce">
                            <span className="text-white text-xs font-bold">3</span>
                          </div>
                          {/* Hover effect */}
                          <div className="absolute inset-0 rounded-xl bg-gradient-to-r from-accent-500/20 to-secondary-500/20 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                        </button>
                        
                        {/* Settings */}
                        <button className="p-3 text-neutral-600 hover:text-neutral-800 hover:bg-white/20 rounded-xl transition-all duration-300 hover-lift group ripple-effect">
                          <span className="text-xl">‚öôÔ∏è</span>
                          {/* Hover effect */}
                          <div className="absolute inset-0 rounded-xl bg-gradient-to-r from-accent-500/20 to-secondary-500/20 opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                        </button>
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Desktop-only spacer for alignment */}
              <div className="hidden lg:block w-16"></div>
            </div>
          </header>
          {/* Page content */}
          <main className="p-6 sm:p-8 md:p-10 w-full max-w-full flex-1 overflow-auto">
            <div className="fade-in">
              {renderActiveComponent()}
            </div>
          </main>
        </div>
      </div>
      <footer className="glass border-t border-white/20 text-center py-6 text-neutral-600 text-sm shadow-elegant">
        <div className="flex items-center justify-center space-x-3">
          <div className="w-6 h-6 bg-gradient-to-br from-secondary-500 to-accent-500 rounded-lg flex items-center justify-center">
            <span className="text-white text-xs font-bold">H</span>
          </div>
          <span>&copy; {new Date().getFullYear()} HealthHub. All rights reserved.</span>
        </div>
      </footer>
    </div>
  );
};

const App = () => {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
};

export default App;
