import React, { useState } from 'react';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import LoginPage from './components/LoginPage';
import RegisterPage from './components/RegisterPage';
import Dashboard from './components/Dashboard';
import Chatbot from './components/Chatbot';
import Monitoring from './components/Monitoring';
import Alerts from './components/Alerts';
import Education from './components/Education';
import Diet from './components/Diet';
import Medication from './components/Medication';
import Profile from './components/Profile';

const AppContent = () => {
  const { isAuthenticated, loading, user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(true);
  const [showRegister, setShowRegister] = useState(false);

  // Show loading state while checking authentication
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-primary-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto mb-4"></div>
          <p className="text-primary-600">Loading...</p>
        </div>
      </div>
    );
  }

  // Show login or register page if not authenticated
  if (!isAuthenticated) {
    if (showRegister) {
      return <RegisterPage onSwitchToLogin={() => setShowRegister(false)} />;
    }
    return <LoginPage onLoginSuccess={() => console.log('Login successful')} onSwitchToRegister={() => setShowRegister(true)} />;
  }

  const navigation = [
    { id: 'dashboard', name: 'Dashboard', icon: '📊', component: Dashboard },
    { id: 'chatbot', name: 'Health Assistant', icon: '💬', component: Chatbot },
    { id: 'monitoring', name: 'Health Tracking', icon: '📈', component: Monitoring },
    { id: 'alerts', name: 'Alerts & Notifications', icon: '🔔', component: Alerts },
    { id: 'education', name: 'Educational Resources', icon: '📚', component: Education },
    { id: 'diet', name: 'Diet Chart', icon: '🍽️', component: Diet },
    { id: 'medication', name: 'Medication Support', icon: '💊', component: Medication }
  ];

  const renderActiveComponent = () => {
    const navItem = navigation.find(nav => nav.id === activeTab);
    if (navItem && navItem.component) {
      const Component = navItem.component;
      return <Component />;
    }
    return <Dashboard />;
  };

  return (
    <div className="min-h-screen bg-primary-50 flex flex-col">
      {/* Mobile menu overlay */}
      {sidebarOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden" onClick={() => setSidebarOpen(false)} />
      )}

      <div className="flex flex-1">
        {/* Sidebar */}
        <div className={`fixed inset-y-0 left-0 z-50 bg-white shadow-lg transform transition-transform duration-300 ease-in-out
          ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'}
          ${sidebarCollapsed ? 'w-20' : 'w-64'}
          lg:translate-x-0 lg:static lg:inset-0 border-r border-primary-200`}
        >
          <div className="flex items-center justify-between h-16 px-4 border-b border-primary-200">
            <button
              onClick={() => setSidebarCollapsed((prev) => !prev)}
              className="flex items-center space-x-2 select-none hover:bg-primary-100 rounded-lg p-2 transition-colors"
              aria-label="Toggle sidebar"
            >
              <span className="text-3xl animate-pulse text-secondary-500">💚</span>
              {!sidebarCollapsed && (
                <span className="text-2xl font-extrabold text-primary-900 tracking-wide transition-all duration-300">HealthHub</span>
              )}
            </button>
            {!sidebarCollapsed && (
              <button
                onClick={logout}
                className="text-sm text-primary-600 hover:text-primary-800 transition-colors p-2 hover:bg-primary-100 rounded-lg"
              >
                Logout
              </button>
            )}
          </div>
          <nav className="mt-6 px-2 flex flex-col space-y-1">
            {navigation.map((item) => (
              <button
                key={item.id}
                onClick={() => {
                  setActiveTab(item.id);
                  setSidebarOpen(false);
                }}
                className={`flex items-center space-x-3 px-2 py-2 rounded-lg mb-1 transition-colors w-full
                  ${activeTab === item.id ? 'bg-primary-100 text-primary-900' : 'text-primary-600 hover:bg-primary-100'}
                  ${sidebarCollapsed ? 'justify-center' : 'justify-start'}
                `}
              >
                <span className="text-xl">{item.icon}</span>
                {!sidebarCollapsed && <span className="font-medium transition-all duration-300">{item.name}</span>}
              </button>
            ))}
          </nav>
        </div>

        {/* Main content */}
        <div className={`flex-1 min-h-screen flex flex-col transition-all duration-300 ${sidebarCollapsed ? 'lg:ml-20' : 'lg:ml-64'}`}>
          {/* Header */}
          <header className="bg-white border-b border-primary-200 shadow-sm">
            <div className="flex items-center justify-between h-16 px-2 sm:px-6">
              <button
                onClick={() => setSidebarOpen(true)}
                className="lg:hidden text-primary-600 hover:text-primary-800"
              >
                <span className="text-xl">☰</span>
              </button>
              <div className="w-full flex justify-center">
                <div className="flex items-center bg-white/80 backdrop-blur-lg rounded-xl shadow-md px-6 py-4 space-x-3 border-l-4 border-secondary-500 max-w-lg w-full border border-primary-200">
                  <span className="text-2xl text-primary-700">👤</span>
                  <span className="text-lg font-semibold text-primary-900">Welcome back, {user?.name || 'User'}!</span>
                </div>
              </div>
              <div className="flex items-center space-x-2">
                <span className="text-sm text-primary-600">📧 {user?.email}</span>
              </div>
            </div>
          </header>
          {/* Page content */}
          <main className="p-2 sm:p-4 md:p-6 w-full max-w-full flex-1 overflow-auto">
            {renderActiveComponent()}
          </main>
        </div>
      </div>
      <footer className="bg-white border-t border-primary-200 text-center py-4 text-primary-600 text-sm shadow-inner">
        &copy; {new Date().getFullYear()} HealthHub. All rights reserved.
      </footer>
    </div>
  );
};

const App = () => {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
};

export default App;
