import React, { useState } from 'react';
import { AuthProvider, useAuth } from './contexts/AuthContext';
import LoginPage from './components/LoginPage';
import RegisterPage from './components/RegisterPage';
import Dashboard from './components/Dashboard';
import Chatbot from './components/Chatbot';
import Monitoring from './components/Monitoring';
import Alerts from './components/Alerts';
import Education from './components/Education';
import Diet from './components/Diet';
import Medication from './components/Medication';
import Profile from './components/Profile';

const AppContent = () => {
  const { isAuthenticated, loading, user, logout } = useAuth();
  const [activeTab, setActiveTab] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [sidebarCollapsed, setSidebarCollapsed] = useState(true);
  const [showRegister, setShowRegister] = useState(false);

  // Show loading state while checking authentication
  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-primary-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-primary-600 mx-auto mb-4"></div>
          <p className="text-primary-600">Loading...</p>
        </div>
      </div>
    );
  }

  // Show login or register page if not authenticated
  if (!isAuthenticated) {
    if (showRegister) {
      return <RegisterPage onSwitchToLogin={() => setShowRegister(false)} />;
    }
    return <LoginPage onLoginSuccess={() => console.log('Login successful')} onSwitchToRegister={() => setShowRegister(true)} />;
  }

  const navigation = [
    { id: 'dashboard', name: 'Dashboard', icon: 'üìä', component: Dashboard },
    { id: 'chatbot', name: 'Health Assistant', icon: 'üí¨', component: Chatbot },
    { id: 'monitoring', name: 'Health Tracking', icon: 'üìà', component: Monitoring },
    { id: 'alerts', name: 'Alerts & Notifications', icon: 'üîî', component: Alerts },
    { id: 'education', name: 'Educational Resources', icon: 'üìö', component: Education },
    { id: 'diet', name: 'Diet Chart', icon: 'üçΩÔ∏è', component: Diet },
    { id: 'medication', name: 'Medication Support', icon: 'üíä', component: Medication },
    { id: 'profile', name: 'Profile', icon: 'üë§', component: Profile }
  ];

  const renderActiveComponent = () => {
    const navItem = navigation.find(nav => nav.id === activeTab);
    if (navItem && navItem.component) {
      const Component = navItem.component;
      return <Component />;
    }
    return <Dashboard />;
  };

  return (
    <div className="min-h-screen bg-primary-50 flex flex-col">
      {/* Mobile menu overlay */}
      {sidebarOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden" onClick={() => setSidebarOpen(false)} />
      )}

      <div className="flex flex-1">
              {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 bg-gradient-to-b from-primary-800 to-primary-900 shadow-2xl transform transition-transform duration-300 ease-in-out
        ${sidebarOpen ? 'translate-x-0' : '-translate-x-full'}
        ${sidebarCollapsed ? 'w-20' : 'w-64'}
        lg:translate-x-0 lg:static lg:inset-0`}
      >
        <div className="flex items-center justify-center h-16 px-4 border-b border-primary-700">
          <button
            onClick={() => setSidebarCollapsed((prev) => !prev)}
            className="flex items-center space-x-2 select-none hover:bg-primary-700 rounded-lg p-2 transition-colors"
            aria-label="Toggle sidebar"
          >
            <span className="text-3xl animate-pulse text-secondary-400">üíö</span>
            {!sidebarCollapsed && (
              <span className="text-2xl font-extrabold text-white tracking-wide transition-all duration-300">HealthHub</span>
            )}
          </button>
        </div>
        <nav className="mt-6 px-2 flex flex-col space-y-1">
          {navigation.map((item) => (
            <button
              key={item.id}
              onClick={() => {
                setActiveTab(item.id);
                setSidebarOpen(false);
              }}
              className={`flex items-center space-x-3 px-3 py-3 rounded-lg mb-1 transition-all duration-200 w-full
                ${activeTab === item.id 
                  ? 'bg-gradient-to-r from-secondary-500 to-secondary-600 text-white shadow-lg transform scale-105' 
                  : 'text-primary-200 hover:bg-primary-700 hover:text-white hover:transform hover:scale-105'
                }
                ${sidebarCollapsed ? 'justify-center' : 'justify-start'}
              `}
            >
              <span className="text-xl">{item.icon}</span>
              {!sidebarCollapsed && <span className="font-medium transition-all duration-300">{item.name}</span>}
            </button>
          ))}
        </nav>
      </div>

        {/* Main content */}
        <div className={`flex-1 min-h-screen flex flex-col transition-all duration-300 ${sidebarCollapsed ? 'lg:ml-20' : 'lg:ml-64'}`}>
          {/* Header */}
          <header className="bg-gradient-to-r from-white via-primary-50 to-secondary-50 border-b border-primary-200 shadow-xl">
            <div className="flex items-center justify-between h-20 px-4 sm:px-8">
              {/* Mobile Menu Button */}
              <button
                onClick={() => setSidebarOpen(true)}
                className="lg:hidden text-primary-600 hover:text-primary-800 p-3 rounded-xl hover:bg-primary-100 transition-all duration-200 shadow-sm"
              >
                <span className="text-2xl">‚ò∞</span>
              </button>

              {/* Main Header Content */}
              <div className="flex-1 flex items-center justify-center">
                <div className="bg-white/90 backdrop-blur-lg rounded-2xl shadow-2xl px-8 py-6 border border-primary-200 max-w-2xl w-full">
                  <div className="flex items-center space-x-6">
                    {/* User Avatar */}
                    <div className="relative">
                      <div className="w-16 h-16 bg-gradient-to-br from-primary-500 via-primary-600 to-secondary-600 rounded-2xl flex items-center justify-center shadow-lg">
                        <span className="text-white text-2xl font-bold">
                          {user?.profile?.firstName?.[0] || user?.email?.[0] || 'üë§'}
                        </span>
                      </div>
                      <div className="absolute -bottom-1 -right-1 w-6 h-6 bg-secondary-500 rounded-full border-2 border-white flex items-center justify-center">
                        <span className="text-white text-xs">üíö</span>
                      </div>
                    </div>

                    {/* User Info */}
                    <div className="flex-1">
                      <h1 className="text-2xl font-bold text-primary-900 mb-1">
                        Welcome back, {user?.profile?.firstName || user?.profile?.username || 'User'}!
                      </h1>
                      <div className="flex items-center space-x-4">
                        <span className="text-primary-600 font-medium">
                          @{user?.profile?.username || user?.email}
                        </span>
                        <div className="w-1 h-1 bg-primary-300 rounded-full"></div>
                        <span className="text-sm text-primary-500">
                          {user?.profile?.age && `${user.profile.age} years old`}
                        </span>
                      </div>
                    </div>

                    {/* Status Badge */}
                    <div className="flex flex-col items-end space-y-2">
                      <div className="bg-gradient-to-r from-secondary-500 to-secondary-600 text-white px-4 py-2 rounded-xl shadow-lg">
                        <span className="text-sm font-semibold">Active</span>
                      </div>
                      <div className="text-xs text-primary-500">
                        {user?.createdAt ? `Member since ${new Date(user.createdAt).getFullYear()}` : 'New Member'}
                      </div>
                    </div>
                  </div>
                </div>
              </div>

              {/* Right Side Actions */}
              <div className="flex items-center space-x-3">
                {/* Notification Bell */}
                <button className="p-3 text-primary-600 hover:text-primary-800 hover:bg-primary-100 rounded-xl transition-all duration-200 shadow-sm">
                  <span className="text-xl">üîî</span>
                </button>
                
                {/* Settings */}
                <button className="p-3 text-primary-600 hover:text-primary-800 hover:bg-primary-100 rounded-xl transition-all duration-200 shadow-sm">
                  <span className="text-xl">‚öôÔ∏è</span>
                </button>
              </div>
            </div>
          </header>
          {/* Page content */}
          <main className="p-2 sm:p-4 md:p-6 w-full max-w-full flex-1 overflow-auto">
            {renderActiveComponent()}
          </main>
        </div>
      </div>
      <footer className="bg-gradient-to-r from-primary-800 to-primary-900 border-t border-primary-700 text-center py-4 text-primary-200 text-sm shadow-lg">
        <div className="flex items-center justify-center space-x-2">
          <span className="text-secondary-400">üíö</span>
          <span>&copy; {new Date().getFullYear()} HealthHub. All rights reserved.</span>
        </div>
      </footer>
    </div>
  );
};

const App = () => {
  return (
    <AuthProvider>
      <AppContent />
    </AuthProvider>
  );
};

export default App;
