import React, { useState } from 'react';

import Dashboard from './components/Dashboard';
import Chatbot from './components/Chatbot';
import Monitoring from './components/Monitoring';
import Alerts from './components/Alerts';
import Education from './components/Education';
import Diet from './components/Diet';
import Medication from './components/Medication';

const App = () => {
  const [activeTab, setActiveTab] = useState('dashboard');
  const [sidebarOpen, setSidebarOpen] = useState(false);

  const navigation = [
    { id: 'dashboard', name: 'Dashboard', icon: '📊', component: Dashboard },
    { id: 'chatbot', name: 'Health Assistant', icon: '💬', component: Chatbot },
    { id: 'monitoring', name: 'Health Tracking', icon: '📈', component: Monitoring },
    { id: 'alerts', name: 'Alerts & Notifications', icon: '🔔', component: Alerts },
    { id: 'education', name: 'Educational Resources', icon: '📚', component: Education },
    { id: 'diet', name: 'Diet Chart', icon: '🍽️', component: Diet },
    { id: 'medication', name: 'Medication Support', icon: '💊', component: Medication }
  ];

  const renderActiveComponent = () => {
    const navItem = navigation.find(nav => nav.id === activeTab);
    if (navItem && navItem.component) {
      const Component = navItem.component;
      return <Component />;
    }
    return <Dashboard />;
  };

  return (
    <div className="min-h-screen bg-gray-100">
      {/* Mobile menu overlay */}
      {sidebarOpen && (
        <div className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden" onClick={() => setSidebarOpen(false)} />
      )}

      {/* Sidebar */}
      <div className={`fixed inset-y-0 left-0 z-50 w-64 bg-white shadow-lg transform transition-transform duration-300 ease-in-out lg:translate-x-0 lg:static lg:inset-0 ${
        sidebarOpen ? 'translate-x-0' : '-translate-x-full'
      }`}>
        <div className="flex items-center justify-between h-16 px-6 border-b border-gray-200">
          <h1 className="text-xl font-bold text-gray-800">HealthHub</h1>
          <button 
            onClick={() => setSidebarOpen(false)}
            className="lg:hidden text-gray-500 hover:text-gray-700"
          >
            <X className="w-6 h-6" />
          </button>
        </div>
        
        <nav className="mt-6 px-3">
          {navigation.map((item) => (
            <button
              key={item.id}
              onClick={() => {
                setActiveTab(item.id);
                setSidebarOpen(false);
              }}
              className={`w-full flex items-center space-x-3 px-3 py-2 rounded-lg mb-2 transition-colors ${
                activeTab === item.id
                  ? 'bg-blue-100 text-blue-700'
                  : 'text-gray-700 hover:bg-gray-100'
              }`}
            >
              <span className="text-xl">{item.icon}</span>
              <span className="font-medium">{item.name}</span>
            </button>
          ))}
        </nav>
      </div>

      {/* Main content */}
      <div className="lg:ml-64">
        {/* Header */}
        <header className="bg-white shadow-sm border-b border-gray-200">
          <div className="flex items-center justify-between h-16 px-6">
            <button
              onClick={() => setSidebarOpen(true)}
              className="lg:hidden text-gray-500 hover:text-gray-700"
            >
              <Menu className="w-6 h-6" />
            </button>
            
            <div className="flex items-center space-x-4">
              <div className="flex items-center space-x-2">
                <User className="w-8 h-8 text-gray-400" />
                <span className="text-gray-700 font-medium">Welcome back!</span>
              </div>
            </div>
          </div>
        </header>

        {/* Page content */}
        <main className="p-6">
          {renderActiveComponent()}
        </main>
      </div>
    </div>
  );
};

export default App;
