import React, { useState } from 'react';
import { useAuth, authFetch } from '../contexts/AuthContext';

const Profile = () => {
  const { user, logout } = useAuth();
  const [isDeleting, setIsDeleting] = useState(false);
  const [showDeleteConfirm, setShowDeleteConfirm] = useState(false);

  const handleDeleteAccount = async () => {
    if (!showDeleteConfirm) {
      setShowDeleteConfirm(true);
      return;
    }

    setIsDeleting(true);
    try {
      const response = await authFetch('http://localhost:5000/api/auth/delete-account', {
        method: 'DELETE'
      });

      if (response.ok) {
        // Account deleted successfully, logout user
        logout();
      } else {
        const data = await response.json();
        alert(data.message || 'Failed to delete account');
      }
    } catch (error) {
      console.error('Delete account error:', error);
      alert('Network error. Please try again.');
    } finally {
      setIsDeleting(false);
      setShowDeleteConfirm(false);
    }
  };

  const handleLogout = () => {
    logout();
  };

  return (
    <div className="space-y-6">
      <div className="bg-white/80 backdrop-blur-lg rounded-xl p-6 border border-primary-200 shadow-lg">
        <div className="flex items-center space-x-4 mb-6">
          <div className="w-16 h-16 bg-primary-100 rounded-full flex items-center justify-center">
            <span className="text-2xl text-primary-600">
              {user?.profile?.firstName?.[0] || user?.email?.[0] || '👤'}
            </span>
          </div>
          <div>
            <h2 className="text-2xl font-bold text-primary-900">
              {user?.profile?.firstName && user?.profile?.lastName 
                ? `${user.profile.firstName} ${user.profile.lastName}`
                : user?.profile?.username || 'User Profile'
              }
            </h2>
            <p className="text-primary-600">@{user?.profile?.username || user?.email}</p>
          </div>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6 mb-6">
          <div className="space-y-4">
            <div>
              <label className="block text-sm font-medium text-primary-700 mb-1">Email</label>
              <p className="text-primary-900 bg-primary-50 p-3 rounded-lg">{user?.email}</p>
            </div>
            
            {user?.profile?.username && (
              <div>
                <label className="block text-sm font-medium text-primary-700 mb-1">Username</label>
                <p className="text-primary-900 bg-primary-50 p-3 rounded-lg">@{user.profile.username}</p>
              </div>
            )}

            {user?.profile?.age && (
              <div>
                <label className="block text-sm font-medium text-primary-700 mb-1">Age</label>
                <p className="text-primary-900 bg-primary-50 p-3 rounded-lg">{user.profile.age} years old</p>
              </div>
            )}
          </div>

          <div className="space-y-4">
            {user?.profile?.gender && (
              <div>
                <label className="block text-sm font-medium text-primary-700 mb-1">Gender</label>
                <p className="text-primary-900 bg-primary-50 p-3 rounded-lg capitalize">{user.profile.gender}</p>
              </div>
            )}

            <div>
              <label className="block text-sm font-medium text-primary-700 mb-1">Member Since</label>
              <p className="text-primary-900 bg-primary-50 p-3 rounded-lg">
                {user?.createdAt ? new Date(user.createdAt).toLocaleDateString() : 'Recently'}
              </p>
            </div>
          </div>
        </div>

        <div className="flex flex-col sm:flex-row gap-4 pt-6 border-t border-primary-200">
          <button
            onClick={handleLogout}
            className="flex-1 bg-primary-600 text-white py-3 px-6 rounded-lg hover:bg-primary-700 transition-colors font-medium flex items-center justify-center space-x-2"
          >
            <span>🚪</span>
            <span>Logout</span>
          </button>
          
          <button
            onClick={handleDeleteAccount}
            disabled={isDeleting}
            className={`flex-1 py-3 px-6 rounded-lg transition-colors font-medium flex items-center justify-center space-x-2 ${
              showDeleteConfirm 
                ? 'bg-red-600 text-white hover:bg-red-700' 
                : 'bg-red-100 text-red-600 hover:bg-red-200'
            } disabled:opacity-50 disabled:cursor-not-allowed`}
          >
            {isDeleting ? (
              <>
                <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-white"></div>
                <span>Deleting...</span>
              </>
            ) : (
              <>
                <span>🗑️</span>
                <span>{showDeleteConfirm ? 'Confirm Delete' : 'Delete Account'}</span>
              </>
            )}
          </button>
        </div>

        {showDeleteConfirm && (
          <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-lg">
            <p className="text-red-800 text-sm">
              ⚠️ <strong>Warning:</strong> This action cannot be undone. All your data will be permanently deleted.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Profile;
